package lecture3;

public class logicaloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    boolean bool1=true,bool2=false;
    System.out.println("bool1 && bool2 = " + (bool1 && bool2));
    System.out.println("bool1 || bool2 = " + (bool1 || bool2)); 
    System.out.println("!(bool1 && bool2) = " + !(bool1 && bool2));
	}

}
